blanee.base = (function() {
  return {
    checkFrame: function(url){
      if(self != top || self.frameElement != null && self.frameElement.tagName.toUpperCase() == "IFRAME"){
        top.location.href = url;
      }
    },
    registerBlurTxt: function(){
      //blur_text
      //blur_hover_field
      $("textarea.blur_hover_field, input.blur_hover_field").each(function(k, e){
        if(!blanee.utils.isBlank($(e).attr("blur_text"))){
          $(e).unbind("focus").focus(function(){
            var blur_txt = $(this).attr("blur_text");
            if($(this).val() == blur_txt){
              $(this).val("");
              $(this).removeClass("light_grey");
            }
          }).unbind("blur").blur(function(){
            var blur_txt = $(this).attr("blur_text");
            if(blanee.utils.isEmptyString($(this).val())){
              $(this).val(blur_txt);
              $(this).addClass("light_grey");
            }
          }).blur();
        }
      });
    },
    registerEvents: function() {
      $("[a]").click(function() {
        var module = $(this).attr("m");
        var db = $(this).attr("db");
        var params = blanee.utils.checkValue($(this).attr("p"), "");
        var callback = $(this).attr("a");

        if (blanee.utils.isBlank(callback)) {
          return false;
        }

        if (blanee.utils.isBlank(module)) {
          // console.error('there is no module defined for the link');
          return false;
        }

        if (!blanee.cfg.loggedIn) {
          window.location.href = blanee.cfg.paths.signup;
          return false;
        }

        str = ('blanee.' + module + '.' + callback + "(" + db + ",\"" + params.replaceAll("\"", "\\\"") + "\")");
        eval(str);
        return false;
      });

      blanee.base.registerBlurTxt();

    },
    registerJSONCallback: function() {
      initialize_confirmations();
      observe_feed_comments();
      blanee.comment.initialize();
    },
    openLink: function(url){
      if(!blanee.utils.isBlank(url)){
        if(url.indexOf("&amp;") > -1){
          window.location.href = url.replace(/&amp;/gm, "&");
        }else{
          window.location.href = url
        }
      }
    }
  };
})();

blanee.slider = (function(){
  var _ele = null;
  var _index = 0;
  var _count = 0;
  var _lis = [];
  var _timer = null;
  var _status = null;
  var _next_path = null;
  var _is_switching = false;

  var getLi = function(){
    var li = $("#slide_pics_" + _index);
    if(li.length == 0){_index = 0; li =  $("#slide_pics_" + _index); }
    return li;
  };

  var nextSlide = function(){
    _is_switching = true;
    var preLi = getLi();
    $(preLi).fadeOut('fast',  function(){
      _index = _index + 1;
      if(_index >= _count){_index = 0;}
      var nextLi = getLi();
      if(nextLi){$(nextLi).fadeIn();}
      do_next();
    });
  };


  var preSlide = function(){
    _is_switching = true;
    var preLi = getLi();
    $(preLi).fadeOut('fast',  function(){
      _index = _index -1 ;
      if(_index < 0 ){_index = _count - 1}
      var nextLi = getLi();
      if(nextLi){$(nextLi).fadeIn();}
      do_next();
    });

  };

  var goNextSlide = function(seconds){
    if(seconds == null || seconds == undefined){seconds = 4000}
    if(_timer != null){window.clearTimeout(_timer);_timer = null;}

    _timer = window.setTimeout(function(){
      if( _is_switching){
        goNextSlide(2000);
      }else{
        nextSlide();
      }
    }, 4000);
  };

  var do_next = function(){
    goNextSlide();
    showStatus();
    _is_switching = false;
    if( _index + 2 < _count && _index > 1  && $("#slide_pics_" + (_index + 2)).length == 0){ 
      var oi = _index ;
      var li = $("#slide_pics_" + oi);
      var pic_id = li.attr("pi");
      jQuery.ajax({dataType:'script', type:'get', url: _next_path + "?pi=" + pic_id + "&index=" + oi + "&format=js" }); 
    }
  };
  var showStatus = function(){
    if(_status != null){
      $(_status).html((_index + 1) + " " + I18n.t("views.business.of") + " " + _count);
    }
  };

  var helper = {
    init: function(ele, status, count, next_path){
      _ele = ele;
      if($(status).length > 0){
        _status = $(status);
      }
      _lis = $(_ele).find("ul li");
      _count = count;
      _next_path = next_path;
      nextSlide();
    },
    pre: function(){ if(!_is_switching){preSlide();}},
    next: function(){if(!_is_switching){nextSlide();}}
  };
  return helper;
})();


