CharacterLimitFactory = function() {
  var clf = new Object();
  clf.limit = 300;
  clf.line_limit = 0;
  clf.line_chars_count = 0;
  clf.elem = null;
  clf.outputElem = null;
  clf.char_only = false;


  clf.checkDefault = function(){

    var elem = clf.elem;
    var limit = clf.limit;
    var outputElem = clf.outputElem;

    var note = elem.val();
    var default_txt = elem.attr("default");
    if(!blanee.utils.isEmptyString(default_txt) && default_txt == note){
      outputElem.html(limit);
    }
  };

  clf.countLines = function() {
    if(clf.line_chars_count > 0 && clf.line_limit > 0 ){
      var elem = clf.elem;
      var limit = clf.limit;
      var outputElem = clf.outputElem;

      var note = elem.val();
      var default_txt = elem.attr("default");

      var arrs = note.split("\n");
      var res = [];

      $.each(arrs, function(index, str){
        if(str.length > clf.line_chars_count){
          var i = 0;
          while(i < str.length){
            res.push(str.substr(i, clf.line_chars_count));
            i = i + clf.line_chars_count;
          }
        }else{
          res.push(str);
        }
      });

      if(res.length > clf.line_limit){
        console.debug(res.slice(0, clf.line_limit).join("\n"));
        elem.val(res.slice(0, clf.line_limit).join("\n"));
      }
    }
  };

  clf.countCharacter = function() {

    clf.countLines();

    var elem = clf.elem;
    var limit = clf.limit;
    var outputElem = clf.outputElem;

    var note = elem.val();
    var default_txt = elem.attr("default");

    var size = note.length;
    if( clf.char_only){size = note.replace(/\s+/g, "").length; }
    if (size > limit) {
      if(clf.char_only){
        var ns = note.replace(/\s+/g, "").substr(0, limit);
        var j = 0;
        var r = [];
        for(var i = 0; i < ns.length; i++){ 
          while(/\s+/.test(note[j])){ r.push(note[j]);j++;}
          if(note[j] == ns[i]){ r.push(note[j]); j++;}
        } 
        elem.val(r.join(""));
      }else{
        elem.val(note.substr(0, limit));
      }
      outputElem.html(0);
    } else {
      outputElem.html(limit - size);
    }
  };


  clf.init = function(elem, limit, outputElem, line_number, line_length, is_char_only) {
    clf.elem = $(elem);
    clf.outputElem = $(outputElem);
    if (limit != null && limit != undefined && limit > 0) {
      clf.limit = limit;
    }

    if( line_number != undefined && line_number != null && (/\d+/).test(""+line_number)){
      clf.line_limit = parseInt("" + line_number);
    }

    if( line_length != undefined && line_length != null && (/\d+/).test("" + line_length)){
      clf.line_chars_count = parseInt("" + line_length);
    }
    if( is_char_only != undefined && is_char_only != null ){
      clf.char_only = is_char_only;
    }

    clf.countCharacter();
    clf.checkDefault();

    clf.elem.change(function() {
      clf.countCharacter();
    }).keyup(function() {
      clf.countCharacter();
    }).unbind("blur").blur(function(){
      clf.countCharacter();
      clf.checkDefault();

      var elem = clf.elem;
      var note = elem.val();
      var default_txt = elem.attr("default");
      if(!blanee.utils.isEmptyString(default_txt) && blanee.utils.isEmptyString(note)){
        elem.val(default_txt);
      }

    }).unbind("focus").focus(function(){
      var elem = clf.elem;
      var note = elem.val();
      var default_txt = elem.attr("default");
      if(!blanee.utils.isEmptyString(default_txt) && default_txt == note){
        elem.val("");
      }

      clf.countCharacter();
      clf.checkDefault();
    });

  };
  return clf;
};

