      var self             = this;
      self.closetimer      = 0;
      self.ulmenu          = 0;
      self.ulmenu          = $(elem).find('ul'); //.eq(0).show();

      $(elem).bind('mouseover', function(){
        if(self.closetimer){ 
          window.clearTimeout(self.closetimer); 
          self.closetimer = null;
        }
        self.ulmenu.show();
      });

      $(self.ulmenu).bind('mouseover', function(){
        if(self.closetimer){ 
          window.clearTimeout(self.closetimer); 
          self.closetimer = null;
        }
        self.ulmenu.show();
      });

      $(self.ulmenu).bind('mouseout', function(){
        self.closetimer = window.setTimeout(function(){self.ulmenu.hide();}, 500);
      });

