blanee.fb = (function(){

  var log = function(str){
    //$.inspect(str, "console");
  };

  var fbUser = null;
  var config =  {
    appId: Cfg.appId,
    status: true,
    cookie: true,
    xfbml: true,
    frictionlessRequests : true,
    channelUrl: Cfg.channelUrl,
    oauth: true};

    var perms =  Cfg.perms;
    var afterSuccess = function(){return true;};
    var afterFailure= function(){return true;};
    var linkExtraParam = function(){return "";};
    var fb_js_loaded = false;
    var linkingStarted = false;

    //----------------------------------------------------------------

    window.fbAsyncInit = function() {
      FB.init(config);
      // run once with current status and whenever the status changes
      if(window.notifyFBOfFirstLogin !== undefined){ notifyFBOfFirstLogin();}
      if(window.executeFB !== undefined){ jQuery(document).ready(executeFB);}
      window.isFBLoaded = true;


      FB.getLoginStatus(function(response){
        ///#        $.inspect(response);
        $("#fb-logout").unbind("click");
        if (response.status === "connected"){
          $("#fb-logout").click(function(){logout(true);});
        }else if(response.status === "not_authorized"){
          $("#fb-logout").click(function(){logout(false);});
        }else{
          $("#fb-logout").click(function(){logout(false);});
        }
      });
      FB.Event.subscribe('auth.statusChange', updateButton);
    };


    //----------------------------------------------------------------


    var load_fb_js = function(){
      if(!fb_js_loaded){
        if(!document.getElementById("facebook-jssdk")){
          var e = document.createElement('script'); e.async = true;

          var local = "fr_FR";
          if(Cfg.locale == "en"){
            local = "en_US" ;
          }

          e.src = document.location.protocol+ '//connect.facebook.net/' + local + '/all.js#xfbml=1&appId='+ Cfg.appId;
          e.id = "facebook-jssdk"
          if($("#fb-root").length < 1 ){
            $(document.body).prepend("<div id='fb-root'></div>");
          }
          $("#fb-root").append($(e));
        }
        fb_js_loaded = true;
      }
    };

    var linking = function(user, extra){
      if(blanee.utils.isBlank(extra)){
        extra = "";

      }
      if(!linkingStarted){
        blanee.fb.markLinkStarted();

        if(!user){ user = fbUser;}

        var url = "";
        if(user !== null){
          url =   "/oauth/start?"+ $.param({"fb_user" : user}) + "&"+ extra;
        }else{
          url = "/oauth/start" + "?" + extra;
        }

        if(Cfg.locale == "en"){
          url = "/en" + url ;
        }

        var customParam = linkExtraParam();
        if(!blanee.utils.isBlank(customParam)){
          url = url + "&" + customParam;
        }
        window.location.href = url;
      }

    };

    var login = function(callback){
      if( FB === undefined){ return;}
      FB.login(function(response) {
        if (response.authResponse) {
          window.setTimeout(function(){
            me(function(){
              if($.isFunction(callback)){

                blanee.fb.markLinkStarted();//Very Very Important. 2013-02-05 ; mak sure the you contains the linking process in the callback ;
                if(callback(response)){
                }
              }else{
                linking();

              }
              //$("#hidden_iframe").attr("src", "http://dev.blanee.com/oauth/start");
            });
          }, 200);
          if(!Cfg.loggedIn && afterSuccess() ){
          }

        } else {
          //user cancelled login or did not grant authorization
        }
      }, {scope:perms});
    };

    var logout = function(needFB){
      if(needFB){
        FB.logout(function(response) {
          window.location.href = Cfg.paths.logout;
          Cfg.fbLoggedIn = false ;
        });
      }else{
        window.location.href = Cfg.paths.logout;
      }
    };
    //----------------------------------------------------------------

    function updateButton(response) {
      //#  var button = document.getElementById('fb-auth');
      if (response.authResponse) {
        Cfg.fbLoggedIn = true ;
      } else {
        afterFailure();
      }
    }

    var fbLoaded = function(){
      return FB && !blanee.utils.isBlank(FB);
    };


    var inviting = false;
    var  sendRequestToManyRecipients =  function(user_ids, exclude_ids, afterComplete){
      if(blanee.utils.isBlank(user_ids)){
        user_ids = "";
      }
      var opts = {
        method: 'apprequests',
        message: I18n.t("I would like to share with you my favorite places on Blanee"),
        to: user_ids,
        filter: ["app_users"],
        data: "data tesinghom"
      };
      if(!blanee.utils.isBlank(exclude_ids)){
        opts.exclude_ids =  exclude_ids;
      }
      FB.ui(opts, function(response){
        inviting =  false;
        if(response && response.request){
          var url = "/oauth/log_request";

          if(Cfg.locale == "en"){
            url = "/en" + url ;
          }

          $.ajax({ url : url , data : {request_id: response.request, inviter : Cfg.fbId}});
        }
        if($.isFunction(afterComplete)){
          afterComplete(response);
        }
      });

    };



    var doInviteFriends = function(afterComplete){
      if(inviting){return ;}
      inviting = true;
      if(Cfg.fbId !== null && fbLoaded() ){
        FB.api({
          method: "fql.query",
          query: "SELECT uid FROM user WHERE uid IN (SELECT uid2 FROM friend WHERE uid1 = " + Cfg.fbId + ") AND is_app_user = 1"},
          function(data){
            var eids = [];
            if($.isArray(data)){
              $.each(data, function(i, v){
                eids.push(v.uid);
              });
            }

            sendRequestToManyRecipients("", eids, afterComplete);
          });
      }

    };

    var inviteFriends = function(afterInvite, afterLogin){
      if(Cfg.fbLoggedIn && Cfg.fbId !== null  && fbLoaded()){
        doInviteFriends(afterInvite);
      }else{
        login(function(){
          doInviteFriends(afterInvite);
          if($.isFunction(afterLogin)){
            return afterLogin();
          }else{
            return false;
          }
        });
      }};

      var me = function( callback){
        FB.api('/me', function(response) {
          fbUser = response;
          Cfg.fbId = response.id;
          Cfg.fbName = response.name;
          if($.isFunction(callback)){
            callback(response);
          }

        });
      };
      var helper = {
        js: function(){
          load_fb_js();
        },
        login: function(callback){
          login(callback);
        },
        logout: function(needFB){
          logout(needFB);
        },
        self: function(){ return FB;},
        afterSuccess: function(callback){
          afterSuccess = function(){
            callback();
          };
        },
        linkExtraParam: function(callback){
          linkExtraParam = function(){
            return callback();
          };
        },
        afterFailure: function(callback){
          afterFailure = function(){
            callback();
          };
        },
        markLinkStarted : function(){
          linkingStarted = true; 
        },
        sendRequestToOneRecipient: function(user_id){
          if(blanee.utils.isBlank(user_id)){
            user_id = "";
          }
          FB.ui({
            method: 'apprequests',
            message: I18n.t("I would like to share with you my favorite places on Blanee"),
            to: user_id
          },
          function(response){ });
        },



        normalInvitation: function(){
          if(Cfg.fbLoggedIn  ){
            window.location.href = Cfg.paths.fb_invite;
          }else{
            window.location.href = Cfg.paths.finish_fb_invite;
          }
          return false;
        },
        initialize: function(){
          load_fb_js();
          $("#fb_btn,#fb-login-button").click(function(){$(this).unbind("click");login();});
          $("#fb-logout").click(function(){
            var logged = $(this).attr("status");
            if(logged == "true"){
              logged = true;
            }else{
              logged = false;
            }
            logout(logged);
          });
          $("#fb-invite-button ").click(function(){
            inviteFriends();
          });

          $("#loggedin-fb-invite-button").click(function(){
            inviteFriends(function(resp){
              if(!blanee.utils.isBlank(resp)){
                blanee.utils.sm(I18n.t("Invitations have been sent successfully"));
              }else{
                blanee.utils.sm("");
              }
            });
          });
          $("#signup-fb-invite-button").click(function(){
            inviteFriends(function(){
              window.location.href = Cfg.paths.finish_fb_invite;
            });
          });
          //#invite_fb_friends
          $("form#send_invitation #btn_participate_game, form#send_invitation #btn_friends_game").click(function(){
            var self = this;
            var form = $(self).parents("form#send_invitation");
            var email = form.find("#email").val();
            var password = form.find("#password").val();

            if(Cfg.fbLoggedIn && !blanee.utils.isEmptyString(email)  && !blanee.utils.isEmptyString(password) ){
              doInviteFriends(function(){
                form.submit();
              });
            }else{
              form.submit();
            }
            return false;
          });

          $("#review-fb-login-button,#review-fb-login-button-link").click(function(){
            login(function(){
              me(function(){
                linking(null, "back=" + encodeURIComponent(Cfg.currentUrl));
                return false;

              });
              return false;
            });
          });


          $("form#send_invitation #skip_invitation").click(function(){
            return helper.normalInvitation();
          });

        }

      };

      return helper;
})();

$(function(){
  blanee.fb.initialize();
});


blanee.pic = (function(){

  var helper = {
    observeBs: function(){
      $("#move_pic_form .success").click(function(){
        $(this).parents("form").submit();
      });

      $("#move_pic_form input[type=text]").blur(function(){
        //$("#current_pic_business_id").click(function(){
        if(!blanee.utils.isEmptyString($(this).val())){
          var permalink = ($(this).val().split("/")).pop();

          if(!blanee.utils.isEmptyString(permalink)){
            $("#move_pic_form img, #move_pic_form .success").hide();
            $("#move_pic_form img.load").fadeIn();
            $.ajax({
              url: "/etablissements/" + permalink,
              dateType: "json",
              success: function(data){
                $("#move_pic_form img").hide();
                 eval("data=" + data);

                if(!blanee.utils.isBlank(data) &&  !blanee.utils.isBlank(data["data"]["id"]) ){
                  $("#current_pic_busienss_id").val(data["data"]["id"]);
                  $("#current_pic_busienss_name").val(data["data"]["id"] + " | " +  data["data"]["name"]);
                  $("#move_pic_form .success").fadeIn();
                }else{
                  $("#current_pic_busienss_id").val("");
                  $("#current_pic_busienss_name").val("");
                  $("#move_pic_form img.error").fadeIn();

                }
              }
            });
          }
        }
      });

    }

  };
  return helper;

})();
